import { Component } from '@angular/core';
import { FormGroup,FormBuilder, FormControl } from '@angular/forms';
import { CrudService } from './crud.service';
import { EmployeeModel } from './employee-model';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'myapp';

  employeeForm = new FormGroup ({
    firstname: new FormControl(),
    lastname: new FormControl(),
  });

  employeeModelObj:EmployeeModel;
  employeeData:any;

constructor(  public crudService:CrudService, private formbuilder:FormBuilder){
  this.createForm(); 
  this.employeeModelObj.firstname="jgg"
   console.log(this.employeeModelObj.firstname)

}

ngOnInIt(){
}

createForm() {
  this.employeeForm = this.formbuilder.group({
    firstname: '',
    lastname: ''
  });
}



submitForm(){
   console.log(this.employeeForm.value.firstname)
  this.crudService.add(this.employeeModelObj)

}




}
