import { Component } from '@angular/core';
import { FormGroup,FormBuilder, FormControl } from '@angular/forms';
import { CrudService } from './crud.service';
import { EmployeeModel } from './employee-model';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'myapp';

  employeeForm = new FormGroup ({
    firstname: new FormControl(),
    lastname: new FormControl(),
  });

  employeeModelObj:EmployeeModel;
  employeeData:any=[];
  singleEmployeeData:any;
  idselected:any;

constructor(  public crudService:CrudService, private formbuilder:FormBuilder){
  this.createForm(); 
   this.getAll();
}

ngOnInIt(){
}

createForm() {
  this.employeeForm = this.formbuilder.group({
    firstname: '',
    lastname: ''
  });
}



submitForm(){
this.employeeModelObj=this.employeeForm.value;
  this.crudService.add(this.employeeModelObj)

}

getAll(){
  this.crudService.getAll().subscribe(
    response => {
      this.employeeData=response;
      console.log(this.employeeData)
    },
    error => {
      console.log(error);
    });
}

edit(id:any){
  console.log(id)
  this.idselected=id;
  this.employeeForm=id;
  console.log(id)

}

update( employeeModelObj:any){
  console.log("in")
   this.crudService.updateinservice(this.idselected,employeeModelObj)
}

delete(id:any){
  this.crudService.delete(id)
}




}
