import { Component } from '@angular/core';
import { EmployeeModel } from '../employee-model';
import { HttpClient } from '@angular/common/http';
import { FormGroup,FormControl } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
user_name:string;
user_password:string

loginForm = new FormGroup({
  user_name: new FormControl(),
  user_password: new FormControl(),
});


private apiServer = "https://devrunner.co.in/machine_test/index.php/web_api/Users";

constructor(private httpClient: HttpClient) { }


login(){
  let Credentials={
   user_name: this.user_name,
   user_password:this.user_password
  }

this.httpClient.get<EmployeeModel[]>(this.apiServer+'/login', Credentials).subscribe(
  Response=>{

  }
)

}



}
<div class="container">

    <form [formGroup]="loginForm" (ngSubmit)="login()">
        <div>
          <label for="user_name">Username</label>
          <input type="text" formControlName="user_name" [(ngModel)]="user_name" >
        </div>
        <div>
          <label for="phone">Password</label>
          <input type="text"  formControlName="user_password" [(ngModel)]="user_password">
        </div>
  <br>

        <div>
          <button class="button" (click)="login()">login</button>
        </div>
    </form>

</div>
