import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { CrudService } from './crud.service';
import { EmployeeModel } from './employee-model';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'myapp';

  editemployee:boolean=false;
  noteditemployee:boolean=true;

  firstname:string;
  lastname:string;
  email:string;
  company:string;
  birthday:any;
  phone:number;
  gender:string;
  password:string;
  conform:string;

  employeeModelObj:EmployeeModel;
  employeeForm: FormGroup



  employeeData:any=[];
  singleEmployeeData:any;
  idselected:any;

constructor(  public crudService:CrudService){

  this.employeeForm = new FormGroup ({
    firstname: new FormControl(this.employeeModelObj.firstname, Validators.required),
    lastname: new FormControl(),
    email:new FormControl(),
    company:new FormControl(),
    birthday:new FormControl(),
    phone:new FormControl(),
    gender:new FormControl(),
    password:new FormControl(),
    conform:new FormControl()
  });



   this.getAll();

}

ngOnInIt(){
}

validate(){

}

submitForm(){
  this.validate();
  console.log(this.employeeForm.value)
this.employeeModelObj=this.employeeForm.value;
  this.crudService.add(this.employeeModelObj)
this.getAll();
this.employeeForm.reset()

}

getAll(){
  this.crudService.getAll().subscribe(
    response => {
      this.employeeData=response;
      console.log(this.employeeData)
    },
    error => {
      console.log(error);
    });
}

edit(employee:any){
  this.editemployee=true;
  this.noteditemployee=false;
  this.firstname=employee.firstname;
  this.lastname=employee.lastname;
  this.email=employee.email;
  this.company=employee.company;
  this.phone=employee.phone;
  this.birthday=employee.birthday;
  this.gender=employee.gender;
  this.password=employee.password
}

update( ){
this.employeeModelObj.firstname=this.firstname;
this.employeeModelObj.lastname=this.lastname;
   this.crudService.updateinservice(this.idselected,this.employeeModelObj)
   this.getAll();
   this.editemployee=false;
   this.noteditemployee=true;
}

delete(employee:any){
  this.crudService.delete(employee.id);
  this.getAll()
}




}
