import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { CrudService } from './crud.service';
import { EmployeeModel } from './employee-model';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'myapp';

  editemployee:boolean=false;
  noteditemployee:boolean=true;

  user_name:string;
 user_email:string;
  user_contact_no:string;
  user_password:string;
  user_gender:string;


  employeeModelObj:EmployeeModel;
  employeeForm = new FormGroup ({
    user_name: new FormControl(),
   user_email: new FormControl(),
    user_contact_no:new FormControl(),
   user_password:new FormControl(),
    user_gender:new FormControl(),
  });

  employeeData:any=[];
  singleEmployeeData:any;
  idselected:any;

constructor(  public crudService:CrudService){

   this.getAll();
}

ngOnInIt(){
}

submitForm(){

  this.crudService.add(this.employeeForm.value)
// this.getAll();
this.employeeForm.reset()

}


getAll(){
  this.crudService.getAll().subscribe(
    response => {
      this.employeeData=response;
      console.log(this.employeeData)
    },
    error => {
      console.log(error);
    });
}

cancel(){
  this.user_name="";
  this.user_email="";
  this.user_contact_no="";
  this.user_password='';
  this.user_gender="";

}

edit(employee:any){
  this.editemployee=true;
  this.noteditemployee=false;

   this.user_name=employee.user_name;
  this.user_email=employee.user_email;
  this.user_contact_no=employee.user_contact_no;
  this.user_gender=employee.user_gender;
  this.user_password=employee.user_password;
}

update( ){
   this.crudService.updateinservice(this.idselected,this.employeeForm.value)
   this.getAll();
   this.editemployee=false;
   this.noteditemployee=true;
}

delete(employee:any){
  this.crudService.delete(employee.id);
  this.getAll()
}

}
